import React, { Component } from 'react';
import Mole from './Mole';

class Playfield extends Component {
  constructor(props){
    super(props);
    setInterval(this.generateRandom, 100);
  }




  state = {
    score: 0,
    active: 1, //Point at the active mole ... (bekijk football oplossing ...)
  }

   fillMoleArray = () =>{
    const moles = [];
    for( let i = 0; i < 12; i++){
      moles.push(<Mole active={this.state.active} id={i} pop={this.generateRandom}/>);
    }

    return moles;
   }

   generateRandom = () =>{
    const nwIndex = Math.floor(Math.random() * 12);
    this.setState({active: nwIndex});
  }

  render() {
    console.log(this.state.moles)
    return (

      <div className="container">
        {this.fillMoleArray()}
      </div>
    );
  }
}

export default Playfield;
