import React, { Component } from 'react';


class Mole extends Component {

  state = {
    value: this.props.index
  }

  showItem = () =>{
    alert(this.props.index);
  }

  render() {
    return (
      <div className="container-item">
        <img src={'img/WAM_Hole.png'} alt="" onClick={this.showItem}/>
      </div>
    );
  }
}

export default Mole;
