import React, { Component } from 'react';


class Mole extends Component {
  constructor(props){
    super(props);
    setInterval(this.showItem, 2000);
  };

  state = {
    value: this.props.index,
    img: this.props.img
  }

  showItem = (props) =>{
    //this.setState({img: 'img/WAM_Mole.png'});
    popUp: Math.floor(Math.random()*11);
    return popUp;
    //this.props.popUp.setState({img: 'img/WAM_Mole.png'});
  }

  render() {
    console.log(this.state.value);
    return (
      <div className="container-item">
        <img src={this.state.img} alt="" onClick={this.showItem}/>
      </div>
    );
  }
}

export default Mole;

