import React, { Component } from 'react';
import Mole from './Mole';
import './main.css'

class App extends Component {
  constructor(props){
    super(props);
    setInterval(this.generateRandom, 1000)
  }

  state = {
    score: 0,
    active: 1, //Point at the active mole ... (bekijk football oplossing ...)
  }

   fillMoleArray = () =>{
    const moles = [];
    for( let i = 0; i < 12; i++){
      moles.push(<Mole active={this.state.active} id={i} />);
    }

    return moles;
   }

   generateRandom = () =>{
    this.setState({active: Math.floor(Math.random() * 12)});
  }

  render() {
    return (
      <div className="App">
        <div className="container">
          {this.fillMoleArray()}
        </div>
        <div className="score">
        <p>Score: {this.state.score}</p>
        </div>
      </div>
    );
  }
}

export default App;
