import React, { Component } from 'react';


class Mole extends Component {
  constructor(props){
    super(props);

  };

  state = {
    value: this.props.index,
    img: this.props.img,
    rndNumb: 0
  }

  showItem = (props) =>{
    //this.setState({img: 'img/WAM_Mole.png'});
    this.setState({rndNumb: Math.floor(Math.random()*11)});
    //popUp: Math.floor(Math.random()*11);
  }

  render() {
    console.log(this.state.value);
    return (
      <div className="container-item">
        <img src={this.state.img} alt="" onClick={this.showItem}/>
      </div>
    );
  }
}

export default Mole;

