import React, { Component } from 'react';

class Mole extends Component {
  state = {
    activeImg: '',
    imgHole: 'img/WAM_Hole.png',
    imgMole: 'img/WAM_Mole.png'
  }

  showImage = () =>{
    if(this.props.id === this.props.active){
      this.setState(this.activeImg, 'img/WAM_Mole.png')
    }else{
      this.setState(this.activeImg, 'img/WAM_Hole.png')
    }
  }

  render(){
    return(

      <div className="container-item">
        {this.showImage}
        <img src={this.state.imgHole} alt=""/>
      </div>
    )
  }
}

export default Mole;
