import React, { Component } from 'react';

class Mole extends Component {
  state = {
    activeImg: ''
  }

  showImage = () =>{
    if(this.props.id === this.props.active){
      this.setState({activeImg: 'img/WAM_Mole.png'})
    }else{
      this.setState({activeImg: 'img/WAM_Hole.png'})
    }
  }

  componentDidMount(){
    this.showImage();
  }

  render(){


    return(
      <div className="container-item">
        {this.props.id === this.props.active ? <img src={this.state.activeImg} alt=""/> : 
        <img src={this.state.activeImg} alt=""/>}
        {/* {this.props.id === this.props.active ? <img src={'img/WAM_Mole.png'} alt=""/> : 
        <img src={'img/WAM_Hole.png'} alt=""/>} */}
      </div>
    )
  }
}

export default Mole;
