import Mole from './Mole';

class Playfield extends Component {
  constructor(props){
    super(props); 
  }

  state = {
    score: 0,
    active: 1
  }

fillField = () =>{
    const moles = [];

    for(let i = 0; i < 12; i++){
      moles.push(<Mole />);
    }
    return moles;
  }

  render() {

    return (
      <div className="container">
        {this.fillField()}
      </div>
    );
  }
}

export default Playfield;
