import React, { Component } from 'react';

class Mole extends Component {
  state = {
    imgHole: 'img/WAM_Hole.png',
    imgMole: 'img/WAM_Mole.png'
  }




  render(){
    return(

      <div className="container-item">
        if(this.props.id === this.props.active){
          <img src={this.state.imgMole} alt=""/>
        }else{
          <img src={this.state.imgHole} alt=""/>
        }

      </div>
    )
  }
}

export default Mole;
